package com.gorgeous.bookshop.controller;

import com.gorgeous.bookshop.bean.ItemInfo;
import com.gorgeous.bookshop.service.ProductService;
import com.gorgeous.bookshop.utils.PowerJSON;
import com.gorgeous.bookshop.utils.RespData;
import io.swagger.annotations.Api;
import io.swagger.annotations.ApiOperation;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.*;

import java.util.ArrayList;
import java.util.List;
import java.util.Map;

@RestController
@RequestMapping("/unit")
@Api(tags = "商品接口")
public class ProductController {

    @Autowired
    ProductService productService;

    @Autowired
    ItemInfo itemInfo;

    @ApiOperation("加载所有商品列表")
    @RequestMapping(value = "/loadItems", method = RequestMethod.GET)
    public List<PowerJSON> loadItems(){
        String str = "{\"image\":\"data:image/jpeg;base64,\",\"description\":\"我真的醉了，许多梦话，就这个春天了。我不再是吟诗作对的诗人，忘了怎么抒情。把每瓣坠入尘埃落絮的故事，就让风去抒情，就让雨去继写，一定要明明媚媚，把岁月的风霜替换。除了爱，我还能有什么呢。\",\"isPicked\":0,\"uid\":\"3e2afc5a943e40338cd59fa338a4edd5\",\"creatAt\":\"2019-12-11 15:40:48\",\"ownerName\":\"admin\",\"price\":\"233\",\"name\":\"item-01\"}\n";
        String str2 = "{\"image\":\"data:image/jpeg;base64,\",\"description\":\"真皮羽绒背心，十分保暖，颜色鲜艳，款式潮流。你需要这件羽绒背心 保护你的温暖和风度！\",\"isPicked\":0,\"uid\":\"0cfba1e9b3ce4b8385cf37e7e5c78b89\",\"creatAt\":\"2020-01-28 16:42:49\",\"ownerName\":\"libai\",\"price\":\"200\",\"name\":\"羽绒背心\"}\n";
        List<PowerJSON> list = new ArrayList<>();
        list.add(PowerJSON.toPowerJSON(str));
        list.add(PowerJSON.toPowerJSON(str2));
        return list;
    }

    @ApiOperation("新增商品")
    @RequestMapping(value = "/submitItem", method = RequestMethod.POST)
    public RespData submitItem(@RequestBody Map<String,Object> map){
        return productService.submitItem(map);
    }

    @ApiOperation("更新商品信息")
    @RequestMapping(value = "/updateProduct", method = RequestMethod.PUT)
    public RespData updateProduct(@RequestBody Map<String,Object> map) {
        return productService.updateProduct(map);
    }

    @ApiOperation("删除商品")
    @RequestMapping(value = "/deleteProduct/{uid}", method = RequestMethod.DELETE)
    public RespData deleteProductUnit(@PathVariable String uid) {
        return productService.deleteProductUnit(uid);
    }
}
